sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },
isDatePassed:function(strDate){
	var currentTime=new GlideDateTime();
	var requestDateTime=new GlideDateTime(strDate);
	if(requestDateTime.before(currentTime)){
		return true;
	}else{
		return false;
	}
},
	isDateToday:function(strDate){
	var todayDate=new GlideDateTime().getLocalDate();
	var whenNeededDate=new GlideDateTime(strDate).getLocalDate();
if(whenNeededDate.compareTo(todayDate)==0)
		return true;
	else
		return false;

},
    type: 'NeedItUtils'
};
